        console.error(`Database initialization error (attempt ${i + 1}):`, error);
        if (i === retries - 1) {
          console.error("Failed to initialize database after all retries");
          // Don't throw error, let the app continue without database initialization
        } else {
          // Wait before retry
          await new Promise(resolve => setTimeout(resolve, 2000));
        }
      }
    }
  };

  // Initialize database asynchronously
  initializeDatabase().catch(console.error);

  // Auth middleware
  const requireAuth = (req: any, res: any, next: any) => {
    if (!req.session.userId) {
      // For demo purposes, use demo user
      req.session.userId = demoUserId;
    }
    next();
  };

  // Auth routes
  app.post('/api/auth/register', async (req: any, res) => {
    try {
      const userData = registerUserSchema.parse(req.body);
      const user = await AuthService.registerUser(userData);
      req.session.userId = user.id;
      res.status(201).json(user);
    } catch (error: any) {
      console.error("Registration error:", error);
      res.status(400).json({ message: error.message });
    }
  });

  app.post('/api/auth/login', async (req: any, res) => {
    try {
      const credentials = loginUserSchema.parse(req.body);
      const user = await AuthService.loginUser(credentials);
      req.session.userId = user.id;
      res.json(user);
    } catch (error: any) {
      console.error("Login error:", error);
      res.status(401).json({ message: error.message });
    }
  });

  app.post('/api/auth/logout', (req: any, res) => {
    req.session.destroy((err: any) => {
      if (err) {
        console.error("Logout error:", err);
        return res.status(500).json({ message: "Failed to logout" });
      }
      res.json({ message: "Logged out successfully" });
    });
  });

  app.get('/api/auth/user', requireAuth, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.session.userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      const { password: _, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Game progress routes
  app.post('/api/game-progress', requireAuth, async (req: any, res) => {
    try {
      const userId = req.session.userId;
      const progressData = insertGameProgressSchema.parse({
        ...req.body,
        userId,
      });

      const progress = await storage.addGameProgress(progressData);

      // Update user XP with variable amount based on score
      const baseXP = 15;
      const bonusXP = Math.floor(progressData.score / 50);
      const totalXP = baseXP + bonusXP;

      const user = await storage.updateUserXP(userId, totalXP);

      // Update streak
      await storage.updateUserStreak(userId);

      // Award coins based on performance
      let coinsEarned = 2; // Base coins per game
      if (progressData.score >= 80) coinsEarned = 5; // Bonus for good performance
      if (progressData.score >= 95) coinsEarned = 8; // Bonus for excellent performance

      await storage.addCoinTransaction({
        userId,
        amount: coinsEarned,
        transactionType: 'game_reward',
        description: `Completed ${progressData.gameType} (Score: ${progressData.score})`,
        gameType: progressData.gameType,
      });

      // Check for new achievements
      const newAchievements = await storage.checkAndUnlockAchievements(userId);

      res.json({ 
        progress, 
        newAchievements,
        coinsEarned,
        message: newAchievements.length > 0 ? "Achievement unlocked!" : `Progress saved! +${coinsEarned} coins`
      });
    } catch (error) {
      console.error("Error saving game progress:", error);
      res.status(500).json({ message: "Failed to save progress" });
    }
  });

  app.get('/api/game-progress', requireAuth, async (req: any, res) => {
    try {
      const userId = req.session.userId;
      const { gameType } = req.query;

      const progress = await storage.getUserGameProgress(userId, gameType as string);
      res.json(progress);
    } catch (error) {
      console.error("Error fetching game progress:", error);
      res.status(500).json({ message: "Failed to fetch progress" });
    }
  });

  app.get('/api/best-scores', requireAuth, async (req: any, res) => {
    try {
      const userId = req.session.userId;
      const bestScores = await storage.getUserBestScores(userId);
      res.json(bestScores);
    } catch (error) {
      console.error("Error fetching best scores:", error);
      res.status(500).json({ message: "Failed to fetch best scores" });
    }
  });

  // Achievement routes
  app.get('/api/achievements', async (req: any, res) => {
    try {
      const achievements = await storage.getAchievements();
      res.json(achievements);
    } catch (error) {
      console.error("Error fetching achievements:", error);
      res.status(500).json({ message: "Failed to fetch achievements" });
    }
  });

  app.get('/api/user-achievements', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const userAchievements = await storage.getUserAchievements(userId);
      res.json(userAchievements);
    } catch (error) {
      console.error("Error fetching user achievements:", error);
      res.status(500).json({ message: "Failed to fetch achievements" });
    }
  });

  // Journal routes
  app.post('/api/journal', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const entryData = insertJournalEntrySchema.parse({
        ...req.body,
        userId,
      });

      const entry = await storage.addJournalEntry(entryData);

      // Award XP for journal entry
      await storage.updateUserXP(userId, 5);

      res.json({ entry, message: "Journal entry saved! +5 XP" });
    } catch (error) {
      console.error("Error saving journal entry:", error);
      res.status(500).json({ message: "Failed to save journal entry" });
    }
  });

  app.get('/api/journal', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const { limit } = req.query;

      const entries = await storage.getUserJournalEntries(userId, limit ? parseInt(limit as string) : undefined);
      res.json(entries);
    } catch (error) {
      console.error("Error fetching journal entries:", error);
      res.status(500).json({ message: "Failed to fetch journal entries" });
    }
  });

  // Skill progress routes
  app.get('/api/skill-progress', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const skillProgress = await storage.getUserSkillProgress(userId);
      res.json(skillProgress);
    } catch (error) {
      console.error("Error fetching skill progress:", error);
      res.status(500).json({ message: "Failed to fetch skill progress" });
    }
  });

  app.put('/api/skill-progress', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const { skillType, level } = req.body;

      await storage.updateSkillProgress(userId, skillType, level);
      res.json({ message: "Skill progress updated" });
    } catch (error) {
      console.error("Error updating skill progress:", error);
      res.status(500).json({ message: "Failed to update skill progress" });
    }
  });

  // Tree management routes
  app.get('/api/trees', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const trees = await storage.getUserTrees(userId);
      res.json(trees);
    } catch (error) {
      console.error("Error fetching trees:", error);
      res.status(500).json({ message: "Failed to fetch trees" });
    }
  });

  app.post('/api/trees/plant', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const { treeType, seedItemId } = req.body;

      // Check if user has the required seed in inventory - ONLY if seedItemId is provided
      if (seedItemId) {
        const inventory = await storage.getUserInventory(userId);
        const seedItem = inventory.find(item => item.storeItemId === seedItemId && (item.quantity || 0) > 0);

        if (!seedItem || (seedItem.quantity || 0) < 1) {
          return res.status(400).json({ message: "You need to purchase a seed first!" });
        }

        // Remove one seed from inventory
        await storage.useInventoryItem(userId, seedItemId, 1);
      }
      // If no seedItemId provided, allow free planting (for demo purposes)

      const tree = await storage.plantTree({
        userId,
        treeType: treeType || 'oak',
        growthStage: 1,
        xpContributed: 0,
      });

      // Track tree planting for achievements
      await storage.addCoinTransaction({
        userId,
        amount: 0, // No coin cost/reward for planting
        transactionType: 'tree_planted',
        description: 'Planted tree'
      });

      // Check for new achievements
      await storage.checkAndUnlockAchievements(userId);

      res.json({ tree, message: "Tree planted successfully!" });
    } catch (error) {
      console.error("Error planting tree:", error);
      res.status(500).json({ message: "Failed to plant tree" });
    }
  });

  app.post('/api/trees/:treeId/water', async (req: any, res) => {
    try {
      const { treeId } = req.params;
      const userId = demoUserId;

      await storage.waterTree(parseInt(treeId));

      // Track watering for achievements
      await storage.addCoinTransaction({
        userId,
        amount: 0, // No coin reward for watering
        transactionType: 'tree_watered',
        description: 'Watered tree'
      });

      // Check for new achievements
      await storage.checkAndUnlockAchievements(userId);

      res.json({ message: "Tree watered!" });
    } catch (error) {
      console.error("Error watering tree:", error);
      res.status(500).json({ message: "Failed to water tree" });
    }
  });

  app.post('/api/trees/:treeId/decorate', async (req: any, res) => {
    try {
      const { treeId } = req.params;
      const { decorationType, storeItemId } = req.body;
      const userId = demoUserId;

      // Use inventory item
      await storage.useInventoryItem(userId, storeItemId, 1);

      // Update tree with decoration
      await storage.decorateTree(parseInt(treeId), decorationType);

      // Return decoration type in response so frontend knows what was added
      res.json({ 
        message: "Tree decorated successfully!", 
        decorationType: decorationType 
      });
    } catch (error) {
      console.error("Error decorating tree:", error);
      res.status(500).json({ message: "Failed to decorate tree" });
    }
  });

  app.post('/api/trees/:treeId/grow', async (req: any, res) => {
    try {
      const { treeId } = req.params;
      const { xpToContribute = 10 } = req.body;
      const userId = demoUserId;

      const parsedTreeId = parseInt(treeId);
      if (isNaN(parsedTreeId)) {
        return res.status(400).json({ message: "Invalid tree ID" });
      }

      // Check if user has enough coins (2 coins to grow)
      const user = await storage.getUser(userId);
      if (!user || (user.coins || 0) < 2) {
        return res.status(400).json({ message: "You need at least 2 coins to grow a tree!" });
      }

      // Deduct 2 coins for growing
      await storage.addCoinTransaction({
        userId,
        amount: -2,
        transactionType: 'tree_growth',
        description: 'Grew tree'
      });

      // Add 10 XP for growing
      await storage.updateUserXP(userId, 10);

      const result = await storage.growTree(parsedTreeId, xpToContribute);

      // Check for new achievements after tree actions
      await storage.checkAndUnlockAchievements(userId);

      res.json({ 
        tree: result.tree, 
        previousStage: result.previousStage,
        message: "Tree grew! -2 coins, +10 XP" 
      });
    } catch (error) {
      console.error("Error growing tree:", error);
      if (error instanceof Error) {
        res.status(400).json({ message: error.message });
      } else {
        res.status(500).json({ message: "Failed to grow tree" });
      }
    }
  });

  // Coin system routes
  app.get('/api/coins/transactions', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const transactions = await storage.getUserCoinTransactions(userId);
      res.json(transactions);
    } catch (error) {
      console.error("Error fetching coin transactions:", error);
      res.status(500).json({ message: "Failed to fetch transactions" });
    }
  });

  // Store routes
  app.get('/api/store', async (req: any, res) => {
    try {
      const { category } = req.query;
      const items = await storage.getStoreItems(category as string);
      res.json(items);
    } catch (error) {
      console.error("Error fetching store items:", error);
      res.status(500).json({ message: "Failed to fetch store items" });
    }
  });

  app.post('/api/store/purchase', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const { itemId, quantity = 1 } = req.body;

      await storage.purchaseItem(userId, itemId, quantity);
      res.json({ message: "Purchase successful!" });
    } catch (error) {
      console.error("Error purchasing item:", error);
      if (error instanceof Error) {
        res.status(400).json({ message: error.message });
      } else {
        res.status(500).json({ message: "Failed to purchase item" });
      }
    }
  });

  app.get('/api/inventory', async (req: any, res) => {
    try {
      const userId = demoUserId;
      const inventory = await storage.getUserInventory(userId);
      res.json(inventory);
    } catch (error) {
      console.error("Error fetching inventory:", error);
      res.status(500).json({ message: "Failed to fetch inventory" });
    }
  });

  // Note: decoration route moved above - removed duplicate

  // Initialize store items route (dev only)
  app.post('/api/admin/seed-store', async (req, res) => {
    try {
      const { seedStore } = await import('./seed');
      await seedStore();
      res.json({ message: 'Store seeded successfully' });
    } catch (error) {
      console.error('Error seeding store:', error);
      res.status(500).json({ message: 'Failed to seed store' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

