@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=Open+Sans:wght@300;400;500;600&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(218, 23%, 97%);
  --foreground: hsl(207, 13%, 12%);
  --muted: hsl(210, 40%, 96%);
  --muted-foreground: hsl(207, 6%, 44%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(207, 13%, 12%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(207, 13%, 12%);
  --border: hsl(207, 13%, 89%);
  --input: hsl(207, 13%, 89%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(210, 40%, 96%);
  --secondary-foreground: hsl(207, 13%, 12%);
  --accent: hsl(210, 40%, 96%);
  --accent-foreground: hsl(207, 13%, 12%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(60, 9%, 98%);
