  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(210, 11%, 15%);
  --muted-foreground: hsl(210, 5%, 65%);
  --popover: hsl(210, 11%, 7%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(210, 11%, 7%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(210, 11%, 15%);
  --input: hsl(210, 11%, 15%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(210, 11%, 15%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(210, 11%, 15%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 63%, 31%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(210, 5%, 84%);

  /* Custom Stimuli colors for dark mode */
  --navy: hsl(207, 90%, 54%);
  --cyan: hsl(182, 100%, 38%);
  --light-gray: hsl(210, 11%, 7%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Open Sans', sans-serif;
  }

  .font-inter {
    font-family: 'Inter', sans-serif;
  }

  .text-navy {
    color: hsl(207, 100%, 18%);
  }

  .bg-navy {
    background-color: hsl(207, 100%, 18%);
  }

  .text-cyan {
    color: hsl(182, 100%, 38%);
  }

  .bg-cyan {
    background-color: hsl(182, 100%, 38%);
  }

  .bg-light-gray {
    background-color: hsl(210, 20%, 98%);
  }

  .border-navy {
    border-color: hsl(207, 100%, 18%);
  }

  .border-cyan {
    border-color: hsl(182, 100%, 38%);
  }

  .from-navy {
    --tw-gradient-from: hsl(207, 100%, 18%);
  }

  .to-cyan {
    --tw-gradient-to: hsl(182, 100%, 38%);
  }

  .dark .text-navy {
    color: hsl(207, 90%, 54%);
  }

  .dark .bg-navy {
    background-color: hsl(207, 90%, 54%);
  }

  .dark .bg-light-gray {
    background-color: hsl(210, 11%, 7%);
  }

  .dark .border-navy {
    border-color: hsl(207, 90%, 54%);
  }

  .dark .from-navy {
    --tw-gradient-from: hsl(207, 90%, 54%);
  }
}

@layer utilities {
  .animate-fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  .animate-slide-up {
    animation: slideUp 0.3s ease-out;
  }

  .animate-pulse-gentle {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes fadeIn {
    0% {
      opacity: 0;
      transform: translateY(10px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slideUp {
    0% {
      opacity: 0;
      transform: translateY(20px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }
}

