    { type: 'pine', name: 'Pine', description: 'Evergreen and resilient', emoji: 'ðŸŒ²' },
    { type: 'maple', name: 'Maple', description: 'Autumn colors', emoji: 'ðŸ' },
    { type: 'birch', name: 'Birch', description: 'Elegant white bark', emoji: 'ðŸŒ³' },
    { type: 'sakura', name: 'Sakura', description: 'Traditional cherry blossom', emoji: 'ðŸŒ¸' },
  ];

  if (isLoading) {
