            animate={{ 
              rotate: [0, 15, -15, 0],
              scale: [1, 1.3, 1],
              opacity: [0.7, 1, 0.7]
            }}
            transition={{ duration: 2.5, repeat: Infinity }}
          >
            âœ¨
          </motion.div>
        )}

        {/* Maturity Crown */}
        {stage >= 5 && (
          <motion.div
            className="absolute top-0 left-1/2 transform -translate-x-1/2 text-amber-300 text-2xl z-50"
            animate={{ 
              scale: [1, 1.4, 1],
              opacity: [0.8, 1, 0.8],
              rotate: [0, 360]
            }}
            transition={{ duration: 3, repeat: Infinity }}
          >
            ðŸŒŸ
          </motion.div>
        )}

        {/* High XP Glow Effect */}
        {xpContributed > 50 && (
          <motion.div
            className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-5"
            style={{
              width: `${80 + (stage * 15)}px`,
              height: `${80 + (stage * 15)}px`,
              background: `radial-gradient(circle, rgba(255,215,0,0.2) 0%, transparent 70%)`,
              borderRadius: '50%'
            }}
            animate={{
              scale: [1, 1.2, 1],
              opacity: [0.3, 0.6, 0.3]
            }}
            transition={{ duration: 3, repeat: Infinity }}
          />
        )}
      </motion.div>
    </div>
  );
};

