            <div className="text-4xl font-bold mb-8">{currentWord}</div>

            <div className="grid grid-cols-4 gap-4 max-w-md mx-auto">
              {colors.map((color, index) => (
                <motion.button
                  key={index}
                  onClick={() => handleColorClick(color)}
