// Local storage utilities for static deployment
export interface StoredUser {
  id: string;
  email: string;
  username: string;
  firstName?: string;
  lastName?: string;
  xp: number;
  level: number;
  streak: number;
  coins: number;
  totalTreesPlanted: number;
  lastLoginDate?: string;
  createdAt: string;
}

export interface StoredGameProgress {
  id: string;
  userId: string;
  gameType: string;
  score: number;
  completedAt: string;
  difficulty: string;
  timeTaken: number;
}

export interface StoredAchievement {
  id: string;
  key: string;
  name: string;
  description: string;
  xpReward: number;
  iconType: string;
  requirement: any;
}

export interface StoredUserAchievement {
  id: string;
  userId: string;
