}

export interface StoredTree {
  id: string;
  userId: string;
  treeType: string;
  growthStage: number;
  xpContributed: number;
  plantedAt: string;
  decorations: string[];
}

export interface StoredInventoryItem {
  id: string;
  userId: string;
  storeItemId: string;
  quantity: number;
  purchasedAt: string;
}

export interface StoredCoinTransaction {
  id: string;
  userId: string;
  amount: number;
  transactionType: string;
